}

return msg;

let oldValue = msg.data.old_state.state;
let newValue = msg.data.new_state.state;

let diff = newValue - oldValue;
msg.diff = diff;

if (diff > 10) {
    return [msg, null];
} else {
    return [null, msg];
}

var dateFormat = require('dateformat');

const lengthSunriseAnimation = 8;
if (msg.payload == "1970-01-01 00:00:00" ) {
    return null;
}

let alarmtime = new Date(msg.payload);

alarmtime.setMinutes(alarmtime.getMinutes() - lengthSunriseAnimation)

//get seconds since midnight

let secondsSinceMidnight = ((alarmtime.getHours() * 60) + alarmtime.getMinutes()) * 60

msg.payload = {}
msg.payload.time = secondsSinceMidnight;
msg.payload.date = dateFormat(alarmtime, "yyyy-mm-dd");
return msg;

if ( msg.data.old_state.state == 'unavailable' 
    && msg.data.new_state.state == 'off') {
    return msg;
} else {
    return null;
}

var d = new Date();
var time = d.getHours();

if (time <= 11) {
    msg.payload = "Guten Morgen Markus."
} else if (time <= 13) {
    msg.payload = "Mahlzeit!"
} else if (time <= 19) {
    msg.payload = "Hallo Markus."
} else if (time > 19) {
    msg.payload = "Guten Abend Markus."
} else {
    return null;
}

return msg;

msg.inzidenz = msg.payload;

if (msg.payload >= 200) {
    msg.ampel = "dunkelrot";

} else if (msg.payload >= 50) {
    msg.ampel = "rot";

} else if (msg.payload >= 35) {
    msg.ampel  = "orange";
}

return msg;

